import React, {Component} from 'react'
import api from '../callApi';
import Loader from '../components/Loader';
import {Link} from 'react-router-dom';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import ListGroup from 'react-bootstrap/ListGroup';

class ItemsBranch extends Component{
	state = {
		branch:false,
		items:false,
	}

	componentDidMount(){
		this.fetchData()
	}

	fetchData = async e => {
		console.log(this.props.match.params.branchId)
		try {
			const branch = await api.branches.get(this.props.match.params.branchId)
			const items = await api.branchItems.getByBranch(this.props.match.params.branchId)
			console.log('branch', branch)
			console.log('items', items)
			this.setState({branch, items})

		} catch (error) {
			console.log('fail getBranch', error)
		}
	}

	handleChange = e => {
		let index_edited = 0;
		let edited_branch = this.state.branches.filter((branch, index) => {
			if(branch.id == e.target.dataset.id){ index_edited = index; }
			return branch.id == e.target.dataset.id
		})[0];
		edited_branch[e.target.name] = e.target.value;

		this.setState({
			branches:[
				...this.state.branches,
			]
		});

		const send_branch_api = {
			"branch_address": edited_branch.address,
			"branch_id": edited_branch.id,
			"branch_name": edited_branch.name,
		}

		console.log('Editado', edited_branch)
		this.save(send_branch_api)
	}

	save = async send_branch_api => {
		try {
			await api.branches.update(send_branch_api);
			console.log('Branch Updated')

		} catch (error) {
			console.log('Fail getAllBranches', error)
		}
	}

	render(){
		if(!this.state.branch || !this.state.items){
			return(
				<Loader/>
			)
		}
		return(
			<div>
				<ListGroup defaultActiveKey="0">
					<ListGroup.Item key="0" variant="primary">
						<Row>
							<Col>
								Nombre
							</Col>
							<Col>
								Código de Barras
							</Col>
							<Col>
								Cantidad en Sucursal
							</Col>
							<Col>
								Precio
							</Col>
						</Row>
					</ListGroup.Item>
					{this.state.items.map(item => {
						return(
							<ListGroup.Item key={item.id} action as={Link} to={`/productos/${item.id}`}>
								<Row>
									<Col>
										{item.item}
									</Col>
									<Col>
										{item.barcode}
									</Col>
									<Col>
										<Form.Control type="text" placeholder="Dirección de Sucursal" value={item.lot} name="lot" onChange={this.handleChange} />
									</Col>
									<Col>
										${item.price}
									</Col>
								</Row>
							</ListGroup.Item>
						)
					})}
				</ListGroup>
				<Button variant="success" type="button" as={Link} to="/productos/agregar" className="mt-4" >
					Agregar Producto
				</Button>
			</div>
		)
	}
}

export default ItemsBranch
